import React, { useState, useEffect } from "react";

import { useSelector, useDispatch } from "react-redux";
import { Link, useLocation } from "react-router-dom";
import { fetchAUser } from "../../store";
import {
  AppBar,
  Toolbar,
  IconButton,
  MenuItem,
  Menu,
  Typography,
  Avatar,
  Badge,
  Button,
} from "@material-ui/core";
import { ShoppingCart, Person, Settings } from "@material-ui/icons";
import Shoppingcart from "../CartCheckout/Cart";
import useStyles from "./styles";

const Navbar = () => {
  const isLoggedIn = useSelector((state) => !!state.auth.id);
  const { auth } = useSelector((state) => state);
  const classes = useStyles();

  const dispatch = useDispatch();

  const { cartReducer } = useSelector((state) => state);
  const [badge, setBadge] = useState(0);

  const userId = useSelector((state) => state.auth);
  const user = useSelector((state) => state.user);

  const location = useLocation();
  console.log(location.pathname);

  useEffect(() => {
    if (cartReducer) {
      setBadge(cartReducer.length);
    }
  }, [cartReducer]);

  useEffect(() => {
    const fetchUser = () => {
      if (!userId) return "loading";
      dispatch(fetchAUser(userId.id)); //user with shopping id
    };
    fetchUser();
  }, [userId]);

  // const { cartReducer } = useSelector((state) => state);

  //remove home and logout when we have functioning components for those, theyre so ugly ;(
  return (
    <div>
      {/* <Typography variant="h3">Federation Store</Typography> */}
      <AppBar position='fixed' color='white'>
        <Toolbar>
          <Avatar
            src='data:image/png;base64,'
            component={Link}
            to='/'
          />
          <Typography
            style={{
              marginLeft: "10px",
              color: "black",
              fontFamily: "Bradley Hand",
              fontSize: "23px",
            }}
            component={Link}
            to='/allsnacks'
          >
            SHOP
          </Typography>
          <div style={{ flexGrow: 1 }} />
          <Typography
            variant='h4'
            style={{ fontFamily: "Bradley Hand", color: "black" }}
            component={Link}
            to='/'
            className={classes.storeName}
          >
            BullsEye Store
          </Typography>
          <div style={{ flexGrow: 1 }} />
          {isLoggedIn ? (
            <div>
              <IconButton component={Link} to='/myaccount'>
                <Person />
              </IconButton>

              {location.pathname !== ("/checkout" || "/confirmation") && (
                <IconButton>
                  <Shoppingcart />
                </IconButton>
              )}

              {auth.isAdmin ? (
                <Link to='/adminpage'>
                  <IconButton>
                    <Settings />
                  </IconButton>
                </Link>
              ) : (
                ""
              )}

              {/* The navbar will show these links after you log in */}
            </div>
          ) : (
            <div>
              <IconButton component={Link} to='/login'>
                <Person />
              </IconButton>

              <IconButton
                component={Link}
                to='/login'
                style={{ marginRight: "10px" }}
              >
                <Badge
                  badgeContent={badge}
                  showZero
                  classes={{ badge: classes.badge }}
                  overlap='rectangular'
                >
                  <ShoppingCart />
                </Badge>
              </IconButton>
              {/* The navbar will show these links before you log in */}
            </div>
          )}
        </Toolbar>
      </AppBar>
      <hr />
    </div>
  );
};

export default Navbar;
